import React from 'react';
import SplashPage from './components/SplashPage';
import QuestionPrompt from './components/QuestionPrompt';
import AnswerForm from './components/AnswerForm';
import { useState, useEffect, useRef } from 'react';

interface Question {
  id: number;
  parameter: number
  prompt: string;
  correctAnswer: string;
  studentAnswer: string;
}

const App: React.FC = () => {
  const [page, setPage] = useState(0);
  const [question, setQuestion] = useState<Question>({
    id: 0,
    parameter: 0,
    prompt: 'Loading...',
    correctAnswer: '',
    studentAnswer: ''
  });
  const tokenRef = useRef<string | null>(new URLSearchParams(window.location.search).get('t'));
  const [message, setMessage] = useState<string>('');

  useEffect(() => {
    const timer = setTimeout(() => {
      if (message==="") setPage(1);
    }, 3000);
    return () => clearTimeout(timer);
  }, [message]);

  const getQuestion = async () => {
    try {
      if (!tokenRef.current) {
        throw new Error('No token found');
      }
      const response = await fetch('http://localhost:8080/question?token=' + tokenRef.current, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*'
        }
      });
      if (!response.ok) {
        throw new Error('Failed to fetch question');
      }
      const data = await response.json();
      tokenRef.current = data.token;
      setQuestion(data.question);
      console.log('Fetched:', question.prompt);
    } catch (error) {
      console.error('Error fetching question:', error);
      setMessage('Error fetching question');
      //setPage(0)
    }
  };

  // Fetch the first question when the app loads
  useEffect(() => {
    getQuestion();
  });

  return (
    <div>
      {page === 0 && <SplashPage message={message} />}
      {page === 1 && <QuestionPrompt question={question} />}
      {page === 1 && <AnswerForm question={question} setPage={setPage} tokenRef={tokenRef}/>}
    </div>
  );
};

export default App;
