import React from 'react';
import SplashPage from './components/SplashPage';
import QuestionPrompt from './components/QuestionPrompt';
import AnswerForm from './components/AnswerForm';
import { useState, useEffect, useCallback, useRef } from 'react';

interface Question {
  prompt: string;
  correctAnswer: string;
  studentAnswer: string;
}

const App: React.FC = () => {
  const [page, setPage] = useState(0);
  const [question, setQuestion] = useState<Question>({
    prompt: 'Loading...',
    correctAnswer: '',
    studentAnswer: ''
  });
  const tokenRef = useRef<string | null>(new URLSearchParams(window.location.search).get('t'));
  const [message, setMessage] = useState<string>('');

  useEffect(() => {
    if (tokenRef.current === null) {
      setMessage('You must launch this app from the assignment link in your LMS.');
    }
  }, []);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (message==="") setPage(1);
    }, 3000);
    return () => clearTimeout(timer);
  }, [message]);

  const getQuestion = useCallback(async () => {
    try {
      if (!tokenRef.current) {
        throw new Error('No token found');
      }
      const response = await fetch('/question?token=' + tokenRef.current, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          //'Authorization': `Bearer ${token}`,
          'Access-Control-Allow-Origin': '*'
        }
      });
      if (!response.ok) {
        throw new Error('Failed to fetch question');
      }
      const data = await response.json();
      tokenRef.current = data.token;
      setQuestion(data.question);
      console.log('Fetched:', question.prompt);
    } catch (error) {
      console.error('Error fetching question:', error);
      setMessage('Error fetching question');
      setPage(0)
    }
  }, []);

  // Fetch the first question when the app loads
  useEffect(() => {
    getQuestion();
  }, [getQuestion]);

  return (
    <div>
      {page === 0 && <SplashPage message={message} />}
      {page > 0 && <QuestionPrompt question={question} />}
      {page === 1 && <AnswerForm setQuestion={setQuestion} />}
    </div>
  );
};

export default App;
