import React from 'react';
import SplashPage from './components/SplashPage';
import QuestionPrompt from './components/QuestionPrompt';
import AnswerForm from './components/AnswerForm';
import { useState, useEffect, useRef } from 'react';

interface Question {
  id: number;
  parameter: number
  prompt: string;
  correctAnswer: string;
  studentAnswer: string;
}

const App: React.FC = () => {
  const [page, setPage] = useState(0);
  const [question, setQuestion] = useState<Question>({
    id: 0,
    parameter: 0,
    prompt: 'Loading...',
    correctAnswer: '',
    studentAnswer: ''
  });
  const tokenRef = useRef<string | null>(new URLSearchParams(window.location.search).get('t'));
  const [message, setMessage] = useState<string>('');

  useEffect(() => {
    const timer = setTimeout(() => {
      setPage(1);
      if (message !== "") setPage(0);
    }, 3000);
    return () => clearTimeout(timer);
  }, [message]);

  const getQuestion = async () => {
    if (page !== 1) return;
    try {
      if (!tokenRef.current) {
        throw new Error('No token found');
      }
      const response = await fetch('/question?token=' + tokenRef.current, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + tokenRef.current
        }
      });
      if (!response.ok) {
        throw new Error('Failed to fetch question');
      }
      const data = await response.json();
      tokenRef.current = data.token;
      setQuestion(data.question);
      console.log('Fetched a question');
    } catch (error) {
      setMessage('Failed to fetch question. Please try again.');
      console.error(error);
    }
  };

  // Fetch the first question when the app loads
  useEffect(() => {
    getQuestion();
  }, [getQuestion]);

  return (
    <div>
      {page === 0 && <SplashPage message={message} />}
      {page > 0 && question.prompt && <QuestionPrompt question={question} />}
      {page > 0 && question.prompt && <AnswerForm tokenRef={tokenRef} question={question} page={page} setPage={setPage} />}
    </div>
  );
};

export default App;
