import React, { useState } from 'react';

interface Question {
    id: number;
    prompt: string;
    correctAnswer: string;
    studentAnswer: string;
}

interface AnswerFormProps {
    tokenRef: React.MutableRefObject<string | null>;
    question: Question;
    setPage: (page: number) => void;
}

const AnswerForm: React.FC<AnswerFormProps> = ({ tokenRef, question, setPage }) => {
  const [studentAnswer, setStudentAnswer] = useState('');
  const [responseHtml, setResponseHtml] = useState<string | null>(null);

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
    setPage(2); // Show the response
    submitAnswer();
  };

  const handleContinue = (event: React.MouseEvent) => {
    event.preventDefault();
    setPage(1); // Go to the next question
  }

  const submitAnswer = async () => {
    try {
      const response = await fetch('/answer', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ id: question.id, answer: studentAnswer }),
      });
      const data = await response.json();
      tokenRef.current = data.token;
      setResponseHtml(data.html);
    } catch (error) {
      console.error('Error submitting answer:', error);
    }
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label>
          Your Answer:
          <input
            type="text"
            value={studentAnswer}
            onChange={(e) => setStudentAnswer(e.target.value)}
          />
        </label>
        <button type="submit">Submit</button>
      </form>
      <div>
        {responseHtml && <div dangerouslySetInnerHTML={{ __html: responseHtml }} />}
      </div>
      <div>
        {responseHtml && <button onClick={handleContinue}>Continue</button>}
      </div>
    </>
  );
};

export default AnswerForm;
