import React from 'react';
import SplashPage from './components/SplashPage';
import QuestionPrompt from './components/QuestionPrompt';
import { useState, useEffect } from 'react';

const App: React.FC = () => {
  const [showSplash, setShowSplash] = useState(true);
  let token = new URLSearchParams(window.location.search).get('t');
  let question = {
    prompt: '',
    correctAnswer: '',
    studentAnswer: ''
  };
  let message = "";
  if (token===null) {
    message = 'You must launch this app from the assignment link in your LMS.';
  }

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowSplash(false);
    }, 3000);
    return () => clearTimeout(timer);
  }, []);

  async function getQuestion() {
    try {
      const response = await fetch('https://chem4ap.appspot.com/question', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      token = data.token;
      question = data.question;
    } catch (error) {
      question = { prompt: 'Error fetching question', correctAnswer: '', studentAnswer: '' };
      console.error('Error fetching question:', error);
    }
  }

  useEffect(() => {
    getQuestion();
  }, []);

  return (
    <div>
      {showSplash && <SplashPage message = {message}/>}
      {!showSplash && <QuestionPrompt question={question} />}
    </div>
  );
};

export default App;
